$(document)
		.ready(
				function() {

					$("#reg-form")
							.validate(
									{
										rules : {
											"firstName" : {
												required : true

											},
											"lastName" : {
												required : true

											},
											"userName" : {
												required : true,
												usercheck : true,
												minlength : 6,
												maxlength : 40
											},
											"password" : {
												required : true,
												pwcheck : true,
												minlength : 8
											},
											"confirmPassword" : {
												required : true,
												equalTo : "#password"

											},
											"emailId" : {
												required : true,
												emailcheck : true,

											}

										},
										messages : {
											"firstName" : {
												required : "FirstName is required",

											},
											"lastName" : {
												required : "LastName is required"

											},
											"userName" : {
												required : "UserName is required",
												usercheck : "UserName must contain alpha numeric characters",
												minlength : "UserName must be atleast 6 characters"
											},
											"password" : {
												required : "password is required",
												pwcheck : "password must contain alpha numeric characters",
												minlength : "password must be atleast 8 characters"

											},
											"confirmPassword" : {
												required : "Repeat password",
												equalTo : "The passwords do not match"

											},
											"emailId" : {
												required : "Email address required",
												emailcheck : "Enter valid email address"

											}

										},

										submitHandler : function(form) {
											form.submit();
										}
									});

					$.validator.addMethod("pwcheck", function(value, element) {
						return /^(?=.*[a-z])[A-Za-z0-9\d=!\-@._*]+$/
								.test(value)
								&& /[a-z]/.test(value) && /\d/.test(value);

					});
					$.validator.addMethod("usercheck",
							function(value, element) {
								return /^(?=.*[a-z])[A-Za-z0-9\d=!\-@._*]+$/
										.test(value)
										&& /[a-z]/.test(value)
										&& /\d/.test(value);

							});

					$.validator
							.addMethod(
									"emailcheck",
									function(value, element) {
										return /^[a-z0-9]+([-._][a-z0-9]+)*@([a-z0-9]+(-[a-z0-9]+)*\.)+[a-z]{2,4}$/
												.test(value)
												&& /^(?=.{1,64}@.{4,64}$)(?=.{6,100}$).*/
														.test(value);

									});

				});

